<div id="lesspass" class="card">
    <div id="menu">
        <div class="card-header text-white bg-dark">
            <div class="row">
                <div class="col-4"><span id="title" class="menu-link pointer">LessPass</span></div>
                <div class="col-8 text-right">
                    <!----><!----><!----><!----><a href="#/whatsnew" class="menu-link pl-3" title="What is new?"><i class="fa fa-lg fa-info-circle"></i></a><a href="#/settings" class="menu-link pl-3" title="Settings"><i class="fa fa-lg fa-cog"></i></a><!----><a href="#/login" class="menu-link pl-3" title="Sign In"><i class="fa fa-lg fa-sign-in pointer"></i></a>
                </div>
            </div>
        </div>
    </div>
    <div id="message">
        <!---->
    </div>
    <div class="lesspass__inner-box card-body">
        <form id="password-generator" novalidate="novalidate">
            <div class="form-group">
                <div class="inputSite">
                    <label for="siteField" class="sr-only">Site</label>
                    <div class="inner-addon left-addon">
                        <i class="fa fa-globe"></i>
                        <div class="awesomplete">
                            <div class="awesomplete">
                                <input id="siteField" type="text" name="siteField" tabindex="0" autocorrect="off" autocapitalize="none" placeholder="Site" class="form-control awesomplete" autocomplete="off" aria-expanded="false" aria-owns="awesomplete_list_2" role="combobox" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;">
                                <ul hidden="" role="listbox" id="awesomplete_list_2"></ul>
                                <span class="visually-hidden" role="status" aria-live="assertive" aria-atomic="true">Type 2 or more characters for results.</span>
                            </div>
                            <ul hidden="" role="listbox" id="awesomplete_list_1"></ul>
                            <span class="visually-hidden" role="status" aria-live="assertive" aria-atomic="true">Type 2 or more characters for results.</span>
                        </div>
                    </div>
                </div>
            </div>
            <div style="display: none;"><label for="username"><input type="text" id="username" name="username" autocomplete="username" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;"></label><label for="password"><input type="password" id="password" name="password" autocomplete="current-password" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;"></label></div>
            <div class="form-group">
                <label for="login" class="sr-only">Username</label>
                <div class="inner-addon left-addon"><i class="fa fa-user"></i><input id="login" type="text" name="login" tabindex="0" autocomplete="off" autocorrect="off" autocapitalize="none" placeholder="Username" class="form-control" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;"></div>
            </div>
            <div class="form-group">
                <div class="masterPassword">
                    <div class="input-group inner-addon left-addon">
                        <label for="passwordField" class="sr-only">Master Password</label><i class="fa fa-lock"></i><input id="passwordField" name="passwordField" type="password" tabindex="0" autocorrect="off" autocapitalize="none" placeholder="Master Password" class="form-control" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;"><!---->
                    </div>
                </div>
            </div>
            <div id="options">
                <div class="form-group row">
                    <div class="col-12">
                        <div class="row">
                            <div class="col"><label for="types">Options</label></div>
                        </div>
                        <div id="types" class="row">
                            <div class="col-3">
                                <div class="form-check"><input id="lowercase__btn" type="checkbox" tabindex="1" class="form-check-input"><label for="lowercase__btn" class="form-check-label"> a-z </label></div>
                            </div>
                            <div class="col-3">
                                <div class="form-check"><input id="uppercase__btn" type="checkbox" tabindex="1" class="form-check-input"><label for="uppercase__btn" class="form-check-label"> A-Z </label></div>
                            </div>
                            <div class="col-3">
                                <div class="form-check"><input id="numbers__btn" type="checkbox" tabindex="1" class="form-check-input"><label for="numbers__btn" class="form-check-label"> 0-9 </label></div>
                            </div>
                            <div class="col-3">
                                <div class="form-check"><input id="symbols__btn" type="checkbox" tabindex="1" class="form-check-input"><label for="symbols__btn" class="form-check-label"> %!@ </label></div>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="form-group row mb-0">
                    <div class="col-5 col-sm-4">
                        <label for="passwordLength">Length</label>
                        <div class="input-group input-group-sm"><span class="input-group-btn"><button id="decreaseLength__btn" tabindex="1" type="button" class="btn btn-primary btn-sm px-2"><small><i class="fa fa-minus"></i></small></button></span><input id="passwordLength" tabindex="1" type="number" min="5" max="35" class="form-control form-control-sm"><span class="input-group-btn"><button id="increaseLength__btn" tabindex="1" type="button" class="btn btn-primary btn-sm px-2"><small><i class="fa fa-plus"></i></small></button></span></div>
                    </div>
                    <div class="col-5 col-sm-4">
                        <label for="passwordCounter" data-balloon-length="large" data-balloon="Increment this value to change the generated password without changing your master options." data-balloon-pos="up">Counter</label>
                        <div class="input-group input-group-sm"><span class="input-group-btn"><button id="decreaseCounter__btn" tabindex="1" type="button" class="btn btn-primary btn-sm px-2"><small><i class="fa fa-minus"></i></small></button></span><input id="passwordCounter" tabindex="1" type="number" min="1" class="form-control form-control-sm"><span class="input-group-btn"><button id="increaseCounter__btn" tabindex="1" type="button" class="btn btn-primary btn-sm px-2"><small><i class="fa fa-plus"></i></small></button></span></div>
                    </div>
                </div>
            </div>
            <div class="form-group mt-4 mb-0">
                <button id="generatePassword__btn" type="submit" tabindex="0" class="btn btn-primary btn-block"> Generate &amp; Copy </button>
                <div class="input-group" style="display: none;"><span class="input-group-btn"><button id="copyPasswordButton" tabindex="0" type="button" class="btn btn-primary"><i class="fa fa-clipboard"></i></button></span><input id="generated-password" type="password" tabindex="-1" class="form-control" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;"><span class="input-group-btn"><button id="revealGeneratedPassword" type="button" tabindex="0" class="btn btn-secondary"><i class="fa fa-eye"></i></button></span><span class="input-group-btn"><button id="sharePasswordProfileButton" type="button" tabindex="0" class="btn btn-secondary"><i class="fa fa-share-alt pointer"></i></button></span></div>
            </div>
        </form>
    </div>
</div>

